using System.Text;
using Microsoft.CodeAnalysis;
using Microsoft.CodeAnalysis.CSharp.Syntax;
using Microsoft.CodeAnalysis.Text;

using static Macaron.Optics.Generator.Helpers;

namespace Macaron.Optics.Generator;

[Generator]
public class OptionalOfGenerator : IIncrementalGenerator
{
    #region Constants
    private const string OptionalOfAttributeSource =
        """
        // <auto-generated />
        #nullable enable

        using System;
        using System.Diagnostics;

        namespace Macaron.Optics
        {
            [Conditional("SOURCE_GENERATOR_ONLY")]
            [AttributeUsage(AttributeTargets.Class, Inherited = false)]
            internal class OptionalOfAttribute : Attribute
            {
                public Type? TargetType { get; }

                public OptionalOfAttribute(Type? targetType = null)
                {
                    TargetType = targetType;
                }
            }
        }

        """;

    private const string OptionalOfAttributeName = "global::Macaron.Optics.OptionalOfAttribute";
    #endregion

    #region IIncrementalGenerator Interface
    public void Initialize(IncrementalGeneratorInitializationContext context)
    {
        context.RegisterPostInitializationOutput(ctx =>
        {
            ctx.AddSource("OptionalOfAttribute.g.cs", SourceText.From(OptionalOfAttributeSource, Encoding.UTF8));
        });

        var visitedTypes = new HashSet<INamedTypeSymbol>(SymbolEqualityComparer.Default);
        IncrementalValuesProvider<LensOfAttributeContext> valuesProvider = context
            .SyntaxProvider
            .CreateSyntaxProvider(
                predicate: static (syntaxNode, _) => syntaxNode is ClassDeclarationSyntax,
                transform: (generatorSyntaxContext, _) => GetClassWithLensOfAttribute(
                    generatorSyntaxContext,
                    OptionalOfAttributeName,
                    visitedTypes
                )
            );

        context.RegisterSourceOutput(
            source: valuesProvider,
            action: (sourceProductionContext, lensOfAttributeContext) => AddSource(
                sourceProductionContext: sourceProductionContext,
                lensOfAttributeContext: lensOfAttributeContext,
                generateLensOfMembers: GenerateOptionalOfMembers
            )
        );
    }
    #endregion
}
